import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity,Alert,Image} from 'react-native';




export default function App() {


  function numeroAleatorio(){
    let sorteio = Math.random() * 10
    sorteio = Math.floor(Math.random() * 10 + 1)
    Alert.alert(String(sorteio))
    getPokemon(sorteio)
  }


  const [ pokemonEscolhido, setPokemonEscolhido ] = useState(1);
  const getPokemon = (sorteio)=> {

    //let sorteio = numeroAleatorio()
    const endpoint = `https://pokeapi.co/api/v2/pokemon/${sorteio}/`
    fetch(endpoint)
        .then(resposta => resposta.json())
          .then( json => {
            const pokemon = {
              nome: json.name,
              img: json.sprites.other["official-artwork"].front_default,
              peso: json.weight,
            };

            setPokemonEscolhido(pokemon);
          })
          .catch(() => {
            Alert.alert('Erro', 'Não foi possível carregar os dados do Pokémon');
          });
      }

  return (
    <View style={styles.container}>
      <View>
        <Image source={require('./assets/pokemon-logo-2.png')} style={styles.logo}/>
      </View>
      <View>
        <Text>{pokemonEscolhido.nome}</Text>
        <Image source={{uri:pokemonEscolhido.img}} style={styles.pokemonImg}/>
      </View>
      <View>
        <TouchableOpacity style={styles.botao} onPress={numeroAleatorio}>
          <Text>Clique Aqui</Text>
        </TouchableOpacity>
      </View>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    resizeMode:'contain',
    width:300,
    height:300,
  },
  pokemonImg: {
    width:200,
    height:200,
  },
  botao: {
    width:200,
    height:70,
    marginTop:100,
    backgroundColor:'#6a6a6a',
    borderRadius:20,
    justifyContent:'center',
    alignItems:'center',


  }
});
